contract EducationalCredential is Ownable {
    ISP public spInstance;
    uint64 public credentialSchemaId;

    event CredentialIssued(address indexed institution, address indexed recipient, string course, uint256 issueDate);

    constructor() Ownable(_msgSender()) {}

    function setSPInstance(address instance) external onlyOwner {
        spInstance = ISP(instance);
    }

    function setCredentialSchemaID(uint64 schemaId) external onlyOwner {
        credentialSchemaId = schemaId;
    }

    function issueCredential(
        address recipient,
        string memory course,
        uint256 issueDate
    ) external returns (uint64) {
        require(credentialSchemaId != 0, "Credential schema not set");

        // Encode the credential data
        bytes memory data = abi.encode(course, issueDate);

        // Create the attestation
        bytes[] memory recipients = new bytes[](1);
        recipients[0] = abi.encode(recipient);

        Attestation memory attestation = Attestation({
            schemaId: credentialSchemaId,
            linkedAttestationId: 0,
            attestTimestamp: 0,
            revokeTimestamp: 0,
            attester: _msgSender(),
            validUntil: 0,
            dataLocation: DataLocation.ONCHAIN,
            revoked: false,
            recipients: recipients,
            data: data
        });

        // Issue the attestation
        uint64 attestationId = spInstance.attest(attestation, "", "", "");

        emit CredentialIssued(_msgSender(), recipient, course, issueDate);

        return attestationId;
    }

    function verifyCredential(address wallet) external view returns (bool, string memory, uint256) {
        // Variables to store the most recent valid attestation
        bool found = false;
        uint64 mostRecentId = 0;
        uint256 mostRecentTimestamp = 0;

        // Iterate through attestations to find the most recent one for the given wallet
        for (uint64 i = 1; ; i++) {
            try spInstance.getAttestation(i) returns (Attestation memory attestation) {
                // Check if the attestation matches our criteria
                if (attestation.schemaId == credentialSchemaId && 
                    !attestation.revoked &&
                    attestation.recipients.length > 0) {

                    address recipient = abi.decode(attestation.recipients[0], (address));

                    if (recipient == wallet && attestation.attestTimestamp > mostRecentTimestamp) {
                        found = true;
                        mostRecentId = i;
                        mostRecentTimestamp = attestation.attestTimestamp;
                    }
                }
            } catch {
                // If we've reached an attestation that doesn't exist, break the loop
                break;
            }
        }

        if (!found) {
            return (false, "", 0);
        }

        // Get the most recent valid attestation
        Attestation memory mostRecentAttestation = spInstance.getAttestation(mostRecentId);

        (string memory course, uint256 issueDate) = abi.decode(mostRecentAttestation.data, (string, uint256));

        return (true, course, issueDate);
    }

    function schemaHook(
        uint64 schemaId,
        bytes calldata data,
        bytes[] calldata recipients
    ) external view returns (bool) {
        require(schemaId == credentialSchemaId, "Invalid schema ID");
        require(recipients.length == 1, "Only one recipient allowed");

        // Decode the credential data
        (string memory course, uint256 issueDate) = abi.decode(data, (string, uint256));

        // Add your custom validation logic here
